package practice;

public class CompoundOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=90;
		a+=10;
		System.out.println("a+=10 is "+a);
		
		
		int b=90;
		b-=10;
		System.out.println("b-=10 is "+b);
		
		int c=90;
		c*=10;
		System.out.println("c*=10 is "+c);
		
		
		int d=90;
		d/=10;
		System.out.println("d/=10 is "+d);
	}

}
