package practice;

public class DecrementOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		System.out.println(a--);
		System.out.println(a);
		
		int b=101;
		System.out.println(--b);
		//System.out.println(b);
		System.out.println(b--);
		System.out.println(b);
	}

}
